mapper.select:
SELECT DISTINCT et.est_id AS est_id ,et.agent_num AS agent_num ,et.est_reg_date AS est_reg_date ,et.est_type AS est_type ,et.est_tra_type AS est_tra_type ,et.est_tra_cond AS est_tra_cond ,et.est_addr AS est_addr ,et.est_m_area AS est_m_area ,et.est_m_floor AS est_m_floor ,et.est_jungong AS est_jungong FROM ESTATE_TB et INNER JOIN EST_OPT_TB ot ON et.EST_ID = ot.EST_ID INNER JOIN EST_OPT_LIST_TB olt ON ot.EST_OPT_NUM = OLT.EST_OPT_NUM
EST_ADDR LIKE '%${est_addr}%' AND et.est_tra_com_date IS NULL
AND et.est_type IN (${est_type})
AND et.est_tra_cond <= ${est_tra_cond_max}
AND et.est_tra_cond >= ${est_tra_cond_min}
AND et.est_m_area <= ${est_m_area_max}
AND et.est_m_area >= ${est_m_area_min}
AND ( TO_CHAR(SYSDATE, 'yyyy') - et.EST_JUNGONG ) < ${est_sungin}
AND ( TO_CHAR(SYSDATE, 'yyyy') - et.EST_JUNGONG ) >= 15
AND et.EST_M_FLOOR IN ( SELECT e2.EST_M_FLOOR FROM ESTATE_TB e2 WHERE e2.EST_M_FLOOR = DECODE(BITAND( ${est_m_floor}, 1),1,1,0) OR e2.EST_M_FLOOR = DECODE(BITAND( ${est_m_floor}, 2),2,-1,0) OR e2.EST_M_FLOOR >=DECODE(BITAND( ${est_m_floor}, 4),4,2,9999999999999999999999999999) )
AND olt.EST_OPT_NAME IN (${tagArray})

SELECT * FROM ESTATE_TB
AGENT_NUM = ${agent_num}
ORDER BY EST_REG_DATE DESC
